mapper.delete:
UPDATE task SET is_delete = 1
id in
#{id}

mapper.select:
SELECT t.task_name AS taskName, t.task_assigned_user_id, tp.task_type_name AS taskTypeName, tg.task_tag_name AS taskTagName, t.create_user_id AS createUserId, t.begin_time AS beginTime, t.end_time AS endTime
FROM task t LEFT JOIN task_tag tg ON t.task_tag_id = tg.id LEFT JOIN task_type tp ON t.task_type_id = tp.id
t.is_delete = 0
and t.task_name like concat('%',#{dto.taskName},'%')
and t.task_type_id = #{dto.taskTypeId}
and t.task_tag_id = #{dto.taskTagId}
and t.create_user_id = #{dto.createUserId}
and t.begin_time >= #{dto.beginTime}
and t.end_time < #{dto.endTime}
